pragma solidity ^0.8.6;

import {SmartWallet} from "./SmartWallet.sol";
import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

contract BaseWallet is SmartWallet {
  using ECDSAUpgradeable for bytes32;

  bytes32 private constant Base_WALLET_STORAGE_POSITION = keccak256("wallet.base.v1");
  struct BaseWalletState {
    address owner;
    uint96 nonce;
  }

  function __BaseWallet_init(address _owner) public initializer {
    __SmartWallet_init_unchained();
    __BaseWallet_init_unchained(_owner);
  }

  function __BaseWallet_init_unchained(address _owner) internal onlyInitializing {
    state().owner = _owner;
  }

  function state() internal pure returns (BaseWalletState storage s) {
    bytes32 position = Base_WALLET_STORAGE_POSITION;
    assembly {
      s.slot := position
    }
  }

  function owner() external view returns (address) {
    return state().owner;
  }

  function nonce() public view virtual override returns (uint256) {
    return state().nonce;
  }

  function _incrementNonce() internal override {
    state().nonce++;
  }

  function _verify(UserOp[] memory userOps, bytes memory _signature) internal view override {}
}
","keccak256":"0x26e4118dfbe6135890be097a13b3c92657a2c94e2ddbcbaadfefcd585384de39","license":"Unlicense"},"contracts/BaseWalletFactory.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.6;

import {BaseWallet} from "./BaseWallet.sol";
import {SmartWalletFactory} from "./SmartWalletFactory.sol";
import {IWallet} from "./interfaces/IWallet.sol";

contract BaseWalletFactory {
  SmartWalletFactory factory;
  BaseWallet wallet;

  constructor(SmartWalletFactory _factory) {
    wallet = new BaseWallet();
    wallet.__BaseWallet_init(address(0));

    factory = _factory;
  }

  function createWallet(address _owner) external returns (IWallet) {
    return factory.createWallet(address(wallet), abi.encodeWithSelector(BaseWallet.__BaseWallet_init.selector, _owner));
  }

  function walletAddress(address _owner, uint256 _nonce) external view returns (address) {
    return factory.walletAddress(address(wallet), abi.encodeWithSelector(BaseWallet.__BaseWallet_init.selector, _owner), _nonce);
  }
}
","keccak256":"0xff3a33f52e6e59dee7c882adc621f6fe1c8caa24d2749d3a259781ecb8537340","license":"Unlicense"},"contracts/SmartWallet.sol":{"content":"//SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.6;

import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {IWallet} from "./interfaces/IWallet.sol";

abstract contract SmartWallet is UUPSUpgradeable, IWallet {
  receive() external payable {
    emit LogReceivedEther(msg.sender, msg.value);
  }

  function __SmartWallet_init() public initializer {
    __SmartWallet_init_unchained();
  }

  function __SmartWallet_init_unchained() internal onlyInitializing {
    if (address(this).balance > 0) {
      emit LogReceivedEther(msg.sender, address(this).balance);
    }
  }

  function _verify(UserOp[] memory userOps, bytes memory _signature) internal view virtual;

  function _incrementNonce() internal virtual;

  function nonce() public view virtual returns (uint256);

  function _authorizeUpgrade(address) internal view override {
    require(msg.sender == address(this));
  }
}
